INSERT INTO `wp_posts` VALUES (369,1,'2025-04-18 10:27:45','2025-04-18 10:27:45','add_filter(\'wp_nav_menu_items\', \'add_profile_badge_menu\', 10, 2);\r\nfunction add_profile_badge_menu($items, $args) {\r\n    if ($args->theme_location == \'primary\') {\r\n        ob_start();\r\n        if (is_user_logged_in()) {\r\n            $user = wp_get_current_user();\r\n            $avatar_url = get_avatar_url($user->ID, array(\'size\' => 32));\r\n            $profile_url = admin_url(\'profile.php\'); // Default WP profile page\r\n            // Alternative: Custom front-end profile page\r\n            // $profile_url = home_url(\'/my-account\'); // Replace with your custom profile page URL\r\n            $logout_url = wp_logout_url(home_url());\r\n            ?>\r\n            <li class=\"profile-badge\">\r\n                <a href=\"#\" class=\"badge-toggle\">\r\n                    <img src=\"<?php echo esc_url($avatar_url); ?>\" alt=\"User Avatar\" class=\"avatar\">\r\n                    <?php echo esc_html($user->display_name); ?>\r\n                </a>\r\n                <ul class=\"badge-dropdown\">\r\n                    <li><a href=\"<?php echo esc_url($profile_url); ?>\">Profile</a></li>\r\n                    <li><a href=\"<?php echo esc_url($logout_url); ?>\">Sign out</a></li>\r\n                </ul>\r\n            </li>\r\n            <?php\r\n        } else {\r\n            $login_url = wp_login_url();\r\n            echo \'<li><a href=\"\' . esc_url($login_url) . \'\">Login</a></li>\';\r\n        }\r\n        $custom_links = ob_get_clean();\r\n        $items .= $custom_links;\r\n    }\r\n    return $items;\r\n}','Header Profile and Logout Links','','draft','closed','closed','','header-profile-and-logout-links','','','2025-04-18 14:21:32','2025-04-18 14:21:32','',0,'http://curator.com/?post_type=wpcode&#038;p=369',0,'wpcode','',0);
INSERT INTO `wp_posts` VALUES (370,1,'2025-04-18 14:03:08','2025-04-18 14:03:08','{\n    \"custom_css[astra]\": {\n        \"value\": \".profile-badge {\\n    position: relative;\\n    display: inline-block;\\n}\\n\\n.avatar {\\n    width: 32px;\\n    height: 32px;\\n    border-radius: 50%;\\n    vertical-align: middle;\\n    margin-right: 8px;\\n}\\n\\n.badge-toggle {\\n    text-decoration: none;\\n    color: #000;\\n    display: flex;\\n    align-items: center;\\n    padding: 5px;\\n}\\n\\n.badge-dropdown {\\n    display: none;\\n    position: absolute;\\n    right: 0;\\n    background: #fff;\\n    border: 1px solid #ddd;\\n    border-radius: 4px;\\n    min-width: 200px;\\n    box-shadow: 0 2px 5px rgba(0,0,0,0.1);\\n    z-index: 1000;\\n    list-style: none;\\n    padding: 0;\\n    margin: 0;\\n}\\n\\n.badge-dropdown li a {\\n    display: block;\\n    padding: 10px 15px;\\n    text-decoration: none;\\n    color: #333;\\n}\\n\\n.badge-dropdown li a:hover {\\n    background-color: #f5f5f5;\\n}\\n\\n.profile-badge:hover .badge-dropdown {\\n    display: block;\\n}\",\n        \"type\": \"custom_css\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-04-18 14:03:08\"\n    }\n}','','','trash','closed','closed','','b8936d4d-5485-4a58-90f6-dfb5d8e99be5','','','2025-04-18 14:03:08','2025-04-18 14:03:08','',0,'http://curator.com/b8936d4d-5485-4a58-90f6-dfb5d8e99be5/',0,'customize_changeset','',0);
INSERT INTO `wp_posts` VALUES (371,1,'2025-04-18 14:03:08','2025-04-18 14:03:08','.profile-badge {\n    position: relative;\n    display: inline-block;\n}\n\n.avatar {\n    width: 32px;\n    height: 32px;\n    border-radius: 50%;\n    vertical-align: middle;\n    margin-right: 8px;\n}\n\n.badge-toggle {\n    text-decoration: none;\n    color: #000;\n    display: flex;\n    align-items: center;\n    padding: 5px;\n}\n\n.badge-dropdown {\n    display: none;\n    position: absolute;\n    right: 0;\n    background: #fff;\n    border: 1px solid #ddd;\n    border-radius: 4px;\n    min-width: 200px;\n    box-shadow: 0 2px 5px rgba(0,0,0,0.1);\n    z-index: 1000;\n    list-style: none;\n    padding: 0;\n    margin: 0;\n}\n\n.badge-dropdown li a {\n    display: block;\n    padding: 10px 15px;\n    text-decoration: none;\n    color: #333;\n}\n\n.badge-dropdown li a:hover {\n    background-color: #f5f5f5;\n}\n\n.profile-badge:hover .badge-dropdown {\n    display: block;\n}','astra','','inherit','closed','closed','','299-revision-v1','','','2025-04-18 14:03:08','2025-04-18 14:03:08','',299,'http://curator.com/?p=371',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (372,1,'2025-04-18 14:04:17','2025-04-18 14:04:17','document.addEventListener(\'DOMContentLoaded\', function() {\r\n    const badgeToggle = document.querySelector(\'.badge-toggle\');\r\n    const dropdown = document.querySelector(\'.badge-dropdown\');\r\n    badgeToggle.addEventListener(\'click\', function(e) {\r\n        e.preventDefault();\r\n        dropdown.style.display = dropdown.style.display === \'block\' ? \'none\' : \'block\';\r\n    });\r\n    document.addEventListener(\'click\', function(e) {\r\n        if (!badgeToggle.contains(e.target)) {\r\n            dropdown.style.display = \'none\';\r\n        }\r\n    });\r\n});','JavaScript Toggle','','draft','closed','closed','','javascript-toggle','','','2025-04-18 14:21:32','2025-04-18 14:21:32','',0,'http://curator.com/?post_type=wpcode&#038;p=372',0,'wpcode','',0);
