INSERT INTO `wp_posts` VALUES (387,1,'2025-04-18 14:35:09','2025-04-18 14:35:09','<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:cover {\"overlayColor\":\"base\",\"isUserOverlayColor\":true,\"isDark\":false,\"metadata\":{\"categories\":[\"twentytwentyfive_page\",\"about\",\"featured\"],\"patternName\":\"twentytwentyfive/page-cv-bio\",\"name\":\"CV/bio\"},\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"right\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\",\"top\":\"var:preset|spacing|60\",\"bottom\":\"var:preset|spacing|60\"},\"margin\":{\"top\":\"0\",\"bottom\":\"0\"}},\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|contrast\"}}}},\"textColor\":\"contrast\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-cover alignfull is-light has-contrast-color has-text-color has-link-color\" style=\"margin-top:0;margin-bottom:0;padding-top:var(--wp--preset--spacing--60);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--60);padding-left:var(--wp--preset--spacing--50)\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-base-background-color has-background-dim-100 has-background-dim\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:columns {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|60\",\"left\":\"var:preset|spacing|80\"}}}} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"65%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:65%\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"justifyContent\":\"stretch\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"textAlign\":\"left\",\"style\":{\"typography\":{\"fontSize\":\"22rem\",\"letterSpacing\":\"-0.03em\",\"fontStyle\":\"normal\",\"fontWeight\":\"300\",\"lineHeight\":\"1.4\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-left\" style=\"font-size:22rem;font-style:normal;font-weight:300;letter-spacing:-0.03em;line-height:1.4\">Hey,</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"className\":\"is-style-text-subtitle\"} -->\n<p class=\"is-style-text-subtitle\">My name is Nora Winslow Keene, and I’m a committed public interest attorney. Living in Denver, Colorado, I’ve spent years championing the rights of underrepresented workers. A graduate of Stanford University, I played a key role in securing critical protections for agricultural laborers, ensuring better wages and access to healthcare. My work has focused on advocating for environmental justice and improving the quality of life for rural communities. Every case I take on is driven by the belief that everyone deserves dignity and fair treatment in the workplace.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"35%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:35%\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"justifyContent\":\"stretch\",\"flexWrap\":\"nowrap\"}} -->\n<div class=\"wp-block-group\"><!-- wp:image {\"aspectRatio\":\"3/4\",\"scale\":\"cover\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://curator.com/wp-content/themes/twentytwentyfive/assets/images/woman-splashing-water.webp\" alt=\"Woman on beach, splashing water.\" style=\"aspect-ratio:3/4;object-fit:cover\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"right\",\"style\":{\"typography\":{\"lineHeight\":\"1.2\"}},\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-right has-x-large-font-size\" style=\"line-height:1.2\"><a href=\"#\">Instagram</a><br><a href=\"#\">LinkedIn</a><br><a href=\"#\">Now</a></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:simple-author-box/sab {\"authorID\":1,\"display_name\":\"kuro\",\"avatar\"